import {
    FETCH_PARTS_START,
    FETCH_PARTS_SUCCESS,
    FETCH_PARTS_FAILURE
} from 'actionTypes'

import {fetchParts as fetchPartsApi} from "api";

export const fetchParts = () => async dispatch => {
    dispatch({type: FETCH_PARTS_START});

    try {
        const parts = await fetchPartsApi();
        dispatch({
            type: FETCH_PARTS_SUCCESS,
            payload: parts
        })
    } catch (err) {
        dispatch({
            type: FETCH_PARTS_FAILURE,
            payload: err,
            error: true
        })

    }
};