import React from 'react';
import R from 'ramda';
import {connect} from 'react-redux';
import {Link, withRouter} from 'react-router';
import {compose} from 'redux';
import classNames from 'classnames';

import {getCategories, getActiveCategoryId} from 'selectors';

const Categories = ({categories, activeCategoryId}) => {
    console.log('activeCategoryId', activeCategoryId);
    const renderCategory = (category, index) => {
        const getActiveState = R.propEq('id', activeCategoryId);
        const linkClass = classNames({
            'list-group-item': true,
            'active': getActiveState(category) // добавлляется класс active, выделяется категория
        });
        return (
            <Link
                to={`/categories/${category.id}`}
                className={linkClass}
                key={index}
            >
                {category.name}
            </Link>
        )
    };

    const renderAllCategory = () => {
        const linkClass = classNames({
            'list-group-item': true,
            'active': R.isNil(activeCategoryId) // когда activeCategoryId - undefined, то применяется класс 'active'
        });
        return (
            <Link
                to={'/'}
                className={linkClass}
            >
                Выбрать все
            </Link>
        )
    };

    return (
        <div className='well'>
            <h4>Категории</h4>
            <div className='list-group'>
                {renderAllCategory()}
                {categories.map((category, index) => renderCategory(category, index))}
            </div>
        </div>
    )
};

const mapStateToProps = (state, ownProps) => ({
    categories: getCategories(state),
    activeCategoryId: getActiveCategoryId(ownProps)
});

export default compose(
    withRouter,
    connect(mapStateToProps, null)
)(Categories)