import React, {Component} from 'react';
import {connect} from 'react-redux';

import {searchPart} from 'actions';

class Search extends Component {
    constructor (props) {
        super(props);
        this.state = {
            value: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange (event) {
        this.setState({
            value: event.target.value
        })
    }

    handleSubmit (event) {
        event.preventDefault();
        this.props.searchPart(this.state.value);
    }

    render () {
        return (
            <div className='well blosd'>
                <div>
                    <h4 className='social'>
                        <a href="https://www.instagram.com/lodkaservicekazan/" target="_blank">
                            <img src="http://allphotographer.at.ua/IMG/inst.png"
                                 alt="Инстаграм"/>
                        </a>
                        <a href="https://lodka-service.ru/" target="_blank">
                            <img src="https://pp.userapi.com/c628326/v628326544/2c048/SNGD4-xRdsk.jpg?ava=1"
                                 alt="Сайт"/>
                        </a>
                        <a href="mailto:remont@lodka-service.ru" target="_blank">
                            <img src="http://www.penosytal.com/Images/but_email.png"
                                 alt="Почта"/>
                        </a><a href="https://vk.com" target="_blank">
                        <img src="http://buyfaster.ru/wp-content/uploads/2018/05/vk.png"
                             alt="Вконтакте"/>
                    </a>
                    </h4>
                </div>
                <h3 className='lead'>Поиск</h3>
                <div className='input-group'>
                    <form onSubmit={this.handleSubmit}>
                        <input
                            onChange={this.handleChange}
                            type='text'
                            className='form-control'
                        />
                    </form>
                    <span className='input-group-btn'>
                        <button className='btn btn-default'>
                            <span className='glyphicon glyphicon-search' />
                        </button>
                    </span>
                </div>
            </div>
        )
    }
}

const mapDispatchToProps = {
    searchPart
};

export default connect(null, mapDispatchToProps)(Search)