import React from 'react';
import R from 'ramda';
import {connect} from 'react-redux';

import {
    getBasketPartsWithCount,
    getTotalBasketPrice
} from 'selectors';

const Basket = ({parts, totalPrice}) => {
    const isBasketEmpty = R.isEmpty(parts); // проверяем пустая корзина или нет

    const renderContent = () => (
        <div>
            {isBasketEmpty && <div>В корзине пусто</div>}

            <div className='table-responsive'>
                <table className='table-bordered table-striped table-condensed cf'>
                    <tbody>
                    {parts.map((part, index) => (
                        <tr
                        key={index}
                        className='item-checkout'
                        >
                            <td className='first-column-checkout'>
                                <img className='img-thumbnail'
                                     src={part.image}
                                     alt={part.name}
                                />
                            </td>
                            <td>{part.name}</td>
                            <td>₽{part.price}</td>
                            <td>{part.count}</td>
                            <td>
                                <span className='delete-cart' />
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
            {
                R.not(isBasketEmpty) &&
                    <div className='row'>
                        <div className='pull-right total-user-checkout'>
                            <b>Итого:</b>
                            ₽{totalPrice}
                        </div>
                    </div>
            }
        </div>
    );

    const renderSidebar = () => (
        <div>
            Sidebar
        </div>
    );
    return (
        <div className='view-container'>
            <div className='container'>
                <div className='row'>
                    <div className='col-md-9'>
                        {renderContent()}
                    </div>
                    <div className='col-md-3 btn-user-checkout'>
                        {renderSidebar()}
                    </div>
                </div>
            </div>
        </div>
    )
};

const mapStateToProps = state => ({
    parts: getBasketPartsWithCount(state),
    totalPrice: getTotalBasketPrice(state)
});

export default connect(mapStateToProps, null)(Basket);