import React, {Component} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router';
import R from 'ramda';

import {
    fetchParts,
    loadMoreParts,
    addPartToBasket
} from 'actions';
import {getParts} from 'selectors';

class Parts extends Component {
    componentDidMount() {
        this.props.fetchParts()
    }

    renderPart (part, index) {
        const {addPartToBasket} = this.props;
        const shortDescription = `${R.take(60, part.description)}...`; // обрезаем описание до 60 символов
        return (
            <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
                <div className='thumbnail'>
                    <img
                        className='img-thumbnail'
                        src={part.image}
                        alt={part.name}
                    />
                    <div className='caption'>
                        <h4 className='pull-right'>₽{part.price}</h4>
                        <h4>
                            <Link to={`/parts/${part.id}`}>
                                {part.name}
                            </Link>
                        </h4>
                        <p>{shortDescription}</p>
                        <p className='itemButton'>
                            <button className='btn btn-primary'
                            onClick={() => addPartToBasket(part.id)}>
                                Купить
                            </button>
                            <Link
                                to={`/parts/${part.id}`}
                                className='btn btn-default'
                            >
                                Подробнее
                            </Link>
                        </p>
                    </div>
                </div>
            </div>
        )
    }

    render() {
        const {parts, loadMoreParts} = this.props;
        return (
            <div>
                <div className='books row'>
                    {parts.map((part, index) => this.renderPart(part, index))}
                </div>
                <div className='row'>
                    <div className='col-md-12'>
                        <button
                            className='pull-right btn btn-primary'
                            onClick={loadMoreParts}
                        >
                            Показать больше
                        </button>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    parts: getParts(state)
});

const mapDispatchToProps = {
    fetchParts,
    loadMoreParts,
    addPartToBasket
};

export default connect(mapStateToProps, mapDispatchToProps)(Parts)