import R from 'ramda';

import {
    ADD_PART_TO_BASKET
} from "actionTypes";

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_PART_TO_BASKET:
            return R.append(payload, state); // новый id добавляется в конец массива state и потом возвращается без мутации
        default:
            return state;
    }
}