import R from 'ramda';

import {
    ADD_PART_TO_BASKET,
    REMOVE_PART_FROM_BASKET,
    CLEAN_BASKET
} from "actionTypes";

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_PART_TO_BASKET:
            return R.append(payload, state); // новый id добавляется в конец массива state и потом возвращается без мутации
        case REMOVE_PART_FROM_BASKET:
            return R.without(R.of(payload), state); //R.of(payload) = [payload] массив с одним значением
        case CLEAN_BASKET:
            return [];
        default:
            return state;
    }
}