import R from 'ramda';

import {
    FETCH_PART_BY_ID_SUCCESS
} from 'actionTypes';

const initialState = {
    id: null // это id товара который мы получили когда зашли на его страницу
};

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PART_BY_ID_SUCCESS:
            return R.merge(state, {
                id: R.prop('id', payload) // получаем id из payload, можно было написать payload.id
            });
        default:
            return state
    }
}