import R from 'ramda' // все ф-ции возвращают новые данные, старые данные не мутирует
import {
    FETCH_PARTS_SUCCESS
} from "actionTypes";

const initialState = {};

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PARTS_SUCCESS:
            const newValue = R.indexBy(R.prop('id'), payload); // 1-й аргумент - поле id хотим видеть как ключ объекта, 2-й аргумент - данные
            return R.merge(state, newValues); // перетираем старые данные на newValue
        default:
            return state
    }
}