// будем хранить все данные относящиеся только к странице запчастей
import R from 'ramda';
import {
    FETCH_PARTS_SUCCESS,
    LOAD_MORE_PARTS_SUCCESS
} from "actionTypes";

const initialState = {
    ids: [] // храним id запчастей, которые отображаются на странице запчастей
};

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PARTS_SUCCESS:
            return R.merge(state, { // вернет новый массив id из payload
                ids: R.pluck('id', payload)
            });
        case LOAD_MORE_PARTS_SUCCESS:
            const ids = R.pluck('id', payload);
            return R.merge(state, { // добавим id которые получили в конец массива
                ids: R.concat(state.ids, ids)
            });
        default:
            return state
    }
}